What I did to get this to work was make some changes to the merge method in the PDFMerger.php file.<br />
<pre><code>public function merge($outputmode = 'browser', $outputpath = 'newfile.pdf')
    {
        if(!isset($this-&gt;_files) || !is_array($this-&gt;_files)): throw new exception(&quot;No PDFs to merge.&quot;); endif;

        $fpdi = new FPDI;

        //merger operations
        foreach($this-&gt;_files as $file)
        {
            $filename  = $file[0];
            $filepages = $file[1];

            $count = $fpdi-&gt;setSourceFile($filename);

            //add the pages
            if($filepages == 'all')
            {
                for($i=1; $i&lt;=$count; $i++)
                {
                    $template   = $fpdi-&gt;importPage($i);
                    $size       = $fpdi-&gt;getTemplateSize($template);

                    if($size['w'] &gt; $size['h'])
                        $format = 'L';
                    if($size['w'] &lt; $size['h'])
                        $format = 'P';

                    $fpdi-&gt;AddPage($format, array($size['w'], $size['h']));
                    $fpdi-&gt;useTemplate($template);
                }
            }
            else
            {
                foreach($filepages as $page)
                {
                    if(!$template = $fpdi-&gt;importPage($page)): throw new exception(&quot;Could not load page '$page' in PDF '$filename'. Check that the page exists.&quot;); endif;
                    $size = $fpdi-&gt;getTemplateSize($template);

                    if($size['w'] &gt; $size['h'])
                        $format = 'L';
                    if($size['w'] &lt; $size['h'])
                        $format = 'P';

                    $fpdi-&gt;AddPage($format, array($size['w'], $size['h']));
                    $fpdi-&gt;useTemplate($template);
                }
            }   
        }

        //output operations
        $mode = $this-&gt;_switchmode($outputmode);

        if($mode == 'S')
        {
            return $fpdi-&gt;Output($outputpath, 'S');
        }
        else
        {
            if($fpdi-&gt;Output($outputpath, $mode))
            {
                return true;
            }
            else
            {
//              throw new exception(&quot;Error outputting PDF to '$outputmode'.&quot;);
                return true;
            }
        }


    }</code></pre>

